// backend/app.js

const express = require('express');
const bodyParser = require('body-parser');
const cors = require('cors');
const pool = require('./db');

const app = express();
const PORT = process.env.PORT || 8080;

app.use(cors());
app.use(bodyParser.json());
app.use(bodyParser.urlencoded({ extended: true }));

// Endpoint para obtener la información de la cuenta después del inicio de sesión
app.post('/api/v1/cuenta/login', async (req, res) => {
  const { numeroCuenta, pin } = req.body;

  try {
    // Consulta SQL para obtener los datos de la cuenta
    const query = `
      SELECT 
        cu.numero_cuenta AS numeroCuenta,
        cu.saldo AS saldo,
        tc.nombre AS tipoCuenta
      FROM 
        cuenta cu
      JOIN 
        tipo_cuenta tc ON cu.tipo_cuenta_id = tc.id
      JOIN 
        afiliado af ON cu.afiliado_id = af.id
      WHERE 
        af.numero_id_afiliado = $1
        AND cu.pin = $2
    `;

    const { rows } = await pool.query(query, [numeroCuenta, pin]);

    if (rows.length === 1) {
      const cuenta = rows[0];
      res.status(200).json(cuenta);
    } else {
      res.status(404).json({ mensaje: 'Credenciales incorrectas. Verifique su número de cuenta y pin.' });
    }
  } catch (error) {
    console.error('Error al realizar la consulta:', error);
    res.status(500).json({ error: 'Error interno del servidor.' });
  }
});

// Iniciar el servidor
app.listen(PORT, () => {
  console.log(`Servidor escuchando en el puerto ${PORT}`);
});
